import React, { Component } from 'react';
import { Text, View, ScrollView, FlatList, Dimensions, ImageBackground, TouchableOpacity, Image } from 'react-native';
import { FontAwesome, SimpleLineIcons, Ionicons, MaterialCommunityIcons, AntDesign } from '@expo/vector-icons';
import userSeeOldRequest from '../Requests/userSeeOldRequests';
import Modal from 'react-native-modal';
import { getFixerProfileRequest } from '../Requests/profileRequest';
import userDeleteCurrentRequest from '../Requests/userDeleteCurrentRequest';
import { userAcceptPrice, userDeclinePrice } from '../Requests/priceNegotiations';
import getPushTokens from '../Requests/getPushTokens';
import sendPushNotification from '../Requests/sendPushNotification';

const { width, height } = Dimensions.get('screen');

class OrderList extends Component {

  state={
    requestIndex: '',
    activeModal: null,
    problem: '',
    serviceType: '',
    acceptor: '',
    fixerFirstName: '',
    fixerLastName: '',
    fixerEmail: '',
    schedule: '',
    payment: '',
    address: '',
    price: ''
  }

  renderModal() {
    const { activeModal } = this.state;

    if (!activeModal) return null;

    return (
        <Modal
        isVisible
        useNativeDriver
        style={styles.modalContainer}
        backdropColor='#C1BEC0'
        onBackButtonPress={() => this.setState({ activeModal: null })}
        onBackdropPress={() => this.setState({ activeModal: null })}
        onSwipeComplete={() => this.setState({ activeModal: null })}
        >
        <View style={styles.modal}>
            <View>
            <Text style={{ fontSize: 16 * 1.5 }}>
                {this.state.serviceType}
            </Text>
            </View>
            <View style={{ paddingVertical: 12 }}>
            <Text style={{ color: '#7D818A', fontSize: 16 * 1.1 }}>
                {this.state.problem}
            </Text>
            </View>
            <View style={styles.modalInfo}>
            <View style={[styles.parkingIcon, {justifyContent: 'flex-start'} ]}>
                <Ionicons name='ios-pricetag' size={16 * 1.1} color='#7D818A' />
                <Text style={{ fontSize: 16 * 1.15 }}> ${this.state.price}</Text>
            </View>
            <View style={[styles.parkingIcon, {justifyContent: 'flex-start'} ]}>
                <Ionicons name='ios-star' size={16 * 1.1} color='#7D818A' />
                <Text style={{ fontSize: 16 * 1.15 }}>4.5</Text>
            </View>
            <View style={[styles.parkingIcon, {justifyContent: 'flex-start'} ]}>
                <Ionicons name='ios-pin' size={16 * 1.1} color='#7D818A' />
                <Text style={{ fontSize: 16 * 1.15 }}>Not Live</Text>
            </View>
            </View>

            <View style={{ alignItems: 'center' }}>
                <Text style={{ fontSize: 15, color: '#A5A5A5', paddingBottom: 5 }}>Information about Repairer</Text>
            </View>
            <View style={styles.profile}>
							<View style={styles.imgView}>
								<Image style={styles.img} source={require('../../assets/Icons/photo.png')} />
							</View>
							<View style={styles.profileText}>
								<Text style={styles.name}>{this.state.fixerFirstName} {this.state.fixerLastName}</Text>
                <Text style={styles.email}>{this.state.fixerEmail}</Text>
							</View>
						</View>

            <View style={{ alignItems: 'center' }}>
                <Text style={{ fontSize: 15, color: '#A5A5A5', paddingBottom: 5 }}>Information about Request</Text>
            </View>
            <View style={{flexDirection: "row"}}>
               <Text style={{fontSize: 15, color: 'red'}}>Scheduled for: </Text>
               <Text>{this.state.schedule}</Text>
            </View>
            <View style={{ flexDirection: 'row'}}> 
               <Text style={{ fontSize: 15, color: 'red'}}>Payment method: </Text>
               <Text>{this.state.payment}</Text>
            </View>
            <View style={{ flexDirection: 'row'}}> 
               <Text style={{ fontSize: 15, color: 'red'}}>Address: </Text>
               <Text>{this.state.address}</Text>
            </View>
        </View>
        </Modal>
    );
    }

  openDetails = (requestIndex) => {
    this.setState({ requestIndex: requestIndex, activeModal: true }, () => {

      const {requestIndex} = this.state;

      userSeeOldRequest(requestIndex).then(res => {
        this.setState({problem: res.request.problem, serviceType: res.request.serviceType, acceptor: res.request.acceptor, schedule: res.request.scheduled, payment: res.request.paymentType, address: res.request.address, price: res.request.price}, () => {
          const fixerId = this.state.acceptor;

          getFixerProfileRequest(fixerId).then(res => {
            this.setState({ fixerFirstName: res.firstName, fixerLastName: res.lastName, fixerEmail: res.email});
          })
        });
      });
    });
  }

  deleteRequest = (requestId) => {
    userDeleteCurrentRequest(requestId).then(res => {
      console.log(res);     
    })
  }

  acceptPrice = (requestId) => {

    userAcceptPrice(requestId).then(res => {
      console.log(res); 
      const serviceType = res.requests.serviceType;
      let acceptor = res.requests.acceptor;

      getPushTokens(serviceType).then(res => {

        var i;
        for (i = 0; i < res.tokens.length; i++) {
          if(res.tokens[i].fixerId === acceptor){
            var to = res.tokens[i].token;
            const title = "Price Accepted";
            const body = "Your Price for Repairing Request accepted";

            sendPushNotification(to, title, body).then(res => {
              console.log(res);       
            })
          }
        }        
      })
    })
  }

  deletePrice = (requestId) => {

    userDeclinePrice(requestId).then(res => {
      console.log(res);
      const serviceType = res.requests.serviceType;
      let acceptor = res.requests.acceptor;

      getPushTokens(serviceType).then(res => {

        var i;
        for (i = 0; i < res.tokens.length; i++) {
          if(res.tokens[i].fixerId === acceptor){
            var to = res.tokens[i].token;
            const title = "Price Declined";
            const body = "Your Price for Repairing Request declined";

            sendPushNotification(to, title, body).then(res => {
              console.log(res);       
            })
          }
        }        
      })
      
    })

  }
  

    render() {
      return (
        <ScrollView>
          <View>
          <View style={{ alignItems: 'center' }}>
            <Text style={{ fontSize: 15, color: '#A5A5A5', paddingBottom: 5 }}>Pending Requests</Text>
          </View>
          {
            this.props.pendingOrders ?
            <FlatList
              id={this.props.orderIndex}
              data={this.props.pendingOrders}
              keyExtractor={(item, index) => index.toString()}
              renderItem={({item} ) => (
                <View>
                <View style={styles.services}>
                  <ImageBackground
                      style={styles.serviceImage}
                      imageStyle={styles.serviceImage}
                      source={require('../../assets/Icons/history.png')}
                  />
                  <View style={styles.serviceDetails}>
                    <View style={{ flex: 1, flexDirection: 'column', justifyContent: 'center' }}>
                        <Text style={{ fontSize: 14, fontWeight: 'bold' }}>
                        {item.serviceType}
                        </Text>
                        <Text style={{ fontSize: 12, color: '#A5A5A5', paddingTop: 5 }}>
                        {item.problem}
                        </Text>
                    </View>
                    <View style={{ flex: 1, flexDirection: 'row', }}>
                        <View style={styles.serviceInfo}>
                            <MaterialCommunityIcons name="cash" color="black" size={23} />
                            <Text style={{ marginLeft: 4, color: '#FFBA5A' }}>{item.paymentType}</Text>
                        </View>
                        <View style={styles.serviceInfo}>
                            <MaterialCommunityIcons name="square-inc-cash" color="black" size={16} />
                            <Text style={{ marginLeft: 4, color: '#FFBA5A' }}>{item.price}</Text>
                        </View>
                        <View style={styles.serviceInfo}>
                        <FontAwesome name="location-arrow" color="#FF7657" size={12} />
                        <Text style={{ marginLeft: 4, color: '#FF7657' }}>
                            Not Live
                        </Text>
                        </View>
                    </View>
                  </View>
                  <View style={{ flex: 0.3, justifyContent: 'center' }}>
                    <TouchableOpacity onPress={() => this.deleteRequest(item._id)}>
                      <AntDesign name="delete" color="#A5A5A5" size={24} />
                    </TouchableOpacity>
                    
                    <TouchableOpacity onPress={() => this.acceptPrice(item._id)}>
                      <AntDesign name="checkcircle" color="#A5A5A5" size={24} />
                    </TouchableOpacity>

                    <TouchableOpacity onPress={() => this.deletePrice(item._id)}>
                      <AntDesign name="closecircle" color="#A5A5A5" size={24} />
                    </TouchableOpacity>
                    
                  </View>
                </View>
            </View>)}
            />:null
          }
          {
            this.props.notAcceptedOrders ?
            <FlatList
              id={this.props.orderIndex}
              data={this.props.notAcceptedOrders}
              keyExtractor={(item, index) => index.toString()}
              renderItem={({item} ) => (
                <View>
                <View style={styles.services}>
                  <ImageBackground
                      style={styles.serviceImage}
                      imageStyle={styles.serviceImage}
                      source={require('../../assets/Icons/history.png')}
                  />
                  <View style={styles.serviceDetails}>
                    <View style={{ flex: 1, flexDirection: 'column', justifyContent: 'center' }}>
                        <Text style={{ fontSize: 14, fontWeight: 'bold' }}>
                        {item.serviceType}
                        </Text>
                        <Text style={{ fontSize: 12, color: '#A5A5A5', paddingTop: 5 }}>
                        {item.problem}
                        </Text>
                    </View>
                    <View style={{ flex: 1, flexDirection: 'row', }}>
                        <View style={styles.serviceInfo}>
                            <MaterialCommunityIcons name="cash" color="black" size={23} />
                            <Text style={{ marginLeft: 4, color: '#FFBA5A' }}>{item.paymentType}</Text>
                        </View>
                        <View style={styles.serviceInfo}>
                            <MaterialCommunityIcons name="square-inc-cash" color="black" size={16} />
                            <Text style={{ marginLeft: 4, color: '#FFBA5A' }}>{item.price}</Text>
                        </View>
                        <View style={styles.serviceInfo}>
                        <FontAwesome name="location-arrow" color="#FF7657" size={12} />
                        <Text style={{ marginLeft: 4, color: '#FF7657' }}>
                            Not Live
                        </Text>
                        </View>
                    </View>
                  </View>
                  <View style={{ flex: 0.3, justifyContent: 'center' }}>
                    <TouchableOpacity onPress={() => this.deleteRequest(item._id)}>
                      <AntDesign name="delete" color="#A5A5A5" size={24} />
                    </TouchableOpacity>
                    
                    <TouchableOpacity onPress={() => this.acceptPrice(item._id)}>
                      <AntDesign name="checkcircle" color="#A5A5A5" size={24} />
                    </TouchableOpacity>

                    <TouchableOpacity onPress={() => this.deletePrice(item._id)}>
                      <AntDesign name="closecircle" color="#A5A5A5" size={24} />
                    </TouchableOpacity>
                    
                  </View>
                </View>
            </View>)}
            />:null
          }
          </View>

          <View>
          <View style={{ alignItems: 'center' }}>
            <Text style={{ fontSize: 15, color: '#A5A5A5', paddingBottom: 5 }}>Accepted Requests</Text>
          </View>
          { 
            this.props.acceptedOrders ?
            <FlatList
              id={this.props.orderIndex}
              data={this.props.acceptedOrders}
              keyExtractor={(item, index) => index.toString()}
              renderItem={({item} ) => (
                <View>
                <View style={styles.services}>
                  <ImageBackground
                      style={styles.serviceImage}
                      imageStyle={styles.serviceImage}
                      source={require('../../assets/Icons/history.png')}
                  />
                  <View style={styles.serviceDetails}>
                    <View style={{ flex: 1, flexDirection: 'column', justifyContent: 'center' }}>
                        <Text style={{ fontSize: 14, fontWeight: 'bold' }}>
                        {item.serviceType}
                        </Text>
                        <Text style={{ fontSize: 12, color: '#A5A5A5', paddingTop: 5 }}>
                        {item.problem}
                        </Text>
                    </View>
                    <View style={{ flex: 1, flexDirection: 'row', }}>
                        <View style={styles.serviceInfo}>
                            <MaterialCommunityIcons name="cash" color="black" size={23} />
                            <Text style={{ marginLeft: 4, color: '#FFBA5A' }}>{item.paymentType}</Text>
                        </View>
                        <View style={styles.serviceInfo}>
                        <FontAwesome name="location-arrow" color="#FF7657" size={12} />
                        <Text style={{ marginLeft: 4, color: '#FF7657' }}>
                            Live
                        </Text>
                        </View>
                    </View>
                  </View>
                  <View style={{ flex: 0.3, justifyContent: 'center' }}>
                    <TouchableOpacity onPress={this.props.details}>
                      <SimpleLineIcons name="info" color="#A5A5A5" size={24} />
                    </TouchableOpacity>

                    <TouchableOpacity onPress={() => this.deleteRequest(item._id)}>
                      <AntDesign name="delete" color="#A5A5A5" size={24} />
                    </TouchableOpacity>
                  </View>
                </View>
            </View>)}
            />:null
          }
          </View>

          <View>
          <View style={{ alignItems: 'center' }}>
            <Text style={{ fontSize: 15, color: '#A5A5A5', paddingBottom: 5 }}>Finished Requests</Text>
          </View>
          { 
            this.props.finishedOrders ?
            <FlatList
              id={this.props.orderIndex}
              data={this.props.finishedOrders}
              keyExtractor={(item, index) => index.toString()}
              renderItem={({item} ) => (
                <View>
                <View style={styles.services}>
                  <ImageBackground
                      style={styles.serviceImage}
                      imageStyle={styles.serviceImage}
                      source={require('../../assets/Icons/history.png')}
                  />
                  <View style={styles.serviceDetails}>
                    <View style={{ flex: 1, flexDirection: 'column', justifyContent: 'center' }}>
                        <Text style={{ fontSize: 14, fontWeight: 'bold' }}>
                        {item.serviceType}
                        </Text>
                        <Text style={{ fontSize: 12, color: '#A5A5A5', paddingTop: 5 }}>
                        {item.problem}
                        </Text>
                    </View>
                    <View style={{ flex: 1, flexDirection: 'row', }}>
                        <View style={styles.serviceInfo}>
                            <MaterialCommunityIcons name="cash" color="black" size={23} />
                            <Text style={{ marginLeft: 4, color: '#FFBA5A' }}>{item.paymentType}</Text>
                        </View>
                        <View style={styles.serviceInfo}>
                        <FontAwesome name="location-arrow" color="#FF7657" size={12} />
                        <Text style={{ marginLeft: 4, color: '#FF7657' }}>
                            Not Live
                        </Text>
                        </View>
                    </View>
                  </View>
                  <View style={{ flex: 0.3, justifyContent: 'center' }}>
                    <TouchableOpacity onPress={() => this.openDetails(item._id)}>
                      <SimpleLineIcons name="info" color="#A5A5A5" size={24} />
                    </TouchableOpacity>
                  </View>
                </View>
            </View>)}
            />:null
          }
          </View>
          {this.renderModal()}
        </ScrollView>
    );
  }
}

