import React, { Component } from "react";
import {View,Text, Image} from "react-native";
import {Header, Left, Icon, Body, Title, Right} from 'native-base';
import { CardSection, SettingInput } from '../components';
import {MaterialIcons} from '@expo/vector-icons';
import {profileRequest, updateUserProfileRequest} from '../Requests/profileRequest';

export default class Settings extends Component {

  static navigationOptions = ({navigation}) => {
    const {state} = navigation;

    return {
      drawerIcon: () => (
        <MaterialIcons name="settings" size={27} style={{ color: 'black' }} />
      ),
      drawerLabel: () => (
        <Text style={{ color: 'black', fontSize: 20 }}>Settings</Text>  
      ),
    }
  }

  constructor(props) {
    super(props);

    this.state = { 
      fName: '', 
      lName: '', 
      email: '',  
      phone: '',
      userId: ''
    };
  }

  componentDidMount() {
    this.setState({ userId: global.userId }, () => {

      const {userId} = this.state
      profileRequest(userId).then(res => {
        this.setState({
          fName: res.firstName,
          lName: res.lastName,
          email: res.email,
          phone: res.phone
        });  
      });
      
    });
  }

  updateProfile = () => {
    const { fName, lName, email, phone, userId } = this.state;

    updateUserProfileRequest(userId, fName, lName, email, phone).then(() => {
      profileRequest(userId).then(res => {
        this.setState({
          fName: res.firstName,
          lName: res.lastName,
          email: res.email,
          phone: res.phone,
        });  
      });    
    })
  }

  render() {
    return (
      <View>
        <Header style={{ backgroundColor: "#97387a"}}>
          <Left style={{ left: 5}}>
            <Icon  name='menu' onPress={() => this.props.navigation.openDrawer()} style={{ color: 'white'}} />
          </Left>
          <Body style={{ flex: 1 }}>
            <Title style={{ color: 'white'}}>Settings</Title>
          </Body>
          <Right style={{ right: 5}}>
            <Icon  name='ios-checkmark' style={{ color: 'white', fontSize: 42}} onPress={() => this.updateProfile()}/>
          </Right>
        </Header>

        <CardSection>
         <Image
         source={require('../../assets/Icons/photo.png')}
         style={styles.ImageIconStyle}
         />
        </CardSection>

        <CardSection>
          <SettingInput
          testID="fname"
          placeholder="Famil"
          label="First Name"
          value={this.state.fName}
          style={{ height: 40, width: 100 }}
          onChangeText={text => this.setState({ fName: text})}
         />
        </CardSection>

        <CardSection>
          <SettingInput
          testID="lname"
          placeholder="Samadli"
          label="Last Name"
          value={this.state.lName}
          style={{ height: 40, width: 100 }}
          onChangeText={text => this.setState({ lName: text})}
         />
        </CardSection>

       <CardSection>
        <SettingInput
          testID="email"
          placeholder="user@gmail.com"
          label="Email"
          value={this.state.email}
          style={{ height: 40, width: 100 }}
          onChangeText={text => this.setState({ email: text})}
        />
       </CardSection>

       <CardSection>
        <SettingInput
           testID="phone"
           placeholder="+(Country code) 50-465-34-43"
           label="Phone"
           value={this.state.phone}
           onChangeText={text => this.setState({ phone: text})}
        />
       </CardSection>

      </View>
    );
  }
}


const styles = {
  ImageIconStyle: {
    padding: 0,
    margin: 5,
    height: 80,
    width: 80,
    resizeMode: 'stretch',
  },
};
